// src/pages/About.jsx
import React from 'react';
import ServiceHero from '../components/ServiceHero';

export default function About() {
  return (
    <>
      {/* Intro Section - bez zmian */}
      <section className="p-8 bg-gray-900 text-gray-100" data-aos="fade-up">
        <div className="max-w-3xl mx-auto text-center space-y-4">
          <h1 className="text-5xl font-extrabold text-neonPrimary">About Me</h1>
          <p className="text-lg text-gray-300">
            I hold a B.Eng in Industrial Automation with a specialization in Power Systems Control (ASE) from
            Wrocław University of Science and Technology (Oct 2020 – Feb 2024), and an M.Sc in Automation IT
            Systems specializing in Information Technologies Applications (ISA) (Mar 2024 – Jul 2025). After
            graduation, I relocated to Warsaw and embarked on my journey as a Junior NOC Operator managing
            OpenStack cloud environments. Previously, I gained hands-on experience programming PLC controllers
            and designing SCADA interfaces.
          </p>
        </div>
      </section>

      {/* Cloud & DevOps Services */}
      <ServiceHero
        id="cloud"
        title="Cloud & DevOps Services"
        subtitle="Robust, scalable infrastructure and CI/CD pipelines"
        description="I deliver end-to-end cloud solutions: designing and maintaining OpenStack environments,
                  automating deployments with Kubernetes, Docker, Ansible, and managing storage with Ceph.
                  Offering fully automated CI/CD, advanced monitoring with Prometheus & Grafana, and GitOps workflows."
      >
        <svg
          className="absolute inset-0 w-72 h-72 mx-auto my-auto text-teal-400"
          viewBox="0 0 320 320"
          fill="none"
          stroke="currentColor"
          strokeWidth="4"
        >
          {/* ∞-loop path */}
          <path
            d="M60,160
               C60,80 140,80 140,160
               S220,240 140,240
               C220,240 300,160 220,160"
            className="animate-pulse"
            strokeDasharray="8 8"
          />

          {/* Left gear */}
          <g className="animate-spin" style={{ transformOrigin: '60px 160px' }}>
            <circle cx="60" cy="160" r="30" />
            {/* teeth */}
            {[...Array(12)].map((_, i) => {
              const ang = (360 / 12) * i;
              return (
                <rect
                  key={i}
                  x="59"
                  y="125"
                  width="2"
                  height="10"
                  transform={`rotate(${ang} 60 160)`}
                  fill="currentColor"
                />
              );
            })}
          </g>

          {/* Right gear */}
          <g className="animate-spin-reverse" style={{ transformOrigin: '220px 160px' }}>
            <circle cx="220" cy="160" r="30" />
            {/* teeth */}
            {[...Array(12)].map((_, i) => {
              const ang = (360 / 12) * i;
              return (
                <rect
                  key={i}
                  x="219"
                  y="125"
                  width="2"
                  height="10"
                  transform={`rotate(${ang} 220 160)`}
                  fill="currentColor"
                />
              );
            })}
          </g>
        </svg>
      </ServiceHero>

      {/* Automation & SRE Excellence */}
      <ServiceHero
        id="automation"
        title="Automation & SRE Excellence"
        subtitle="Infrastructure as Code & Reliable Operations"
        description="Specializing in writing Ansible playbooks, Terraform modules, and Helm charts.
                  I implement SRE best practices: incident response, runbooks, chaos testing,
                  and ensure 99.9% uptime through proactive monitoring and alerting."
      >
        <svg
          className="absolute inset-0 w-80 h-80 mx-auto my-auto text-purple-400"
          viewBox="0 0 320 320"
          fill="none"
          stroke="currentColor"
          strokeWidth="3"
        >
          {/* Desk */}
          <rect x="20" y="260" width="280" height="10" fill="#222" rx="4" />

          {/* Chair & silhouette */}
          <g transform="translate(160, 200)">
            <rect x="-15" y="0" width="30" height="20" fill="#333" rx="3" />
            <rect x="-20" y="-40" width="40" height="40" fill="#333" rx="5" />
            <circle cx="0" cy="-60" r="10" fill="#333" />
          </g>

          {/* Monitors */}
          {[ { x: 50, y: 50, anim: 'animate-pulse', shape: 'rect' },
             { x: 200, y: 50, anim: 'animate-bounce', shape: 'polyline', points: '0,40 20,20 40,60 60,30' },
             { x: 50, y: 150, anim: 'animate-ping', shape: 'circle', cx: 20, cy: 20, r: 8},
             { x: 200, y: 150, anim: 'animate-pulse', shape: 'line', x1:0,y1:40,x2:60,y2:0 }
          ].map((m, i) => (
            <g key={i} transform={`translate(${m.x}, ${m.y})`}>
              {/* monitor frame */}
              <rect x="0" y="0" width="80" height="60" fill="#111" stroke="currentColor" rx="4"/>
              <rect x="30" y="60" width="20" height="10" fill="#222" rx="2"/>
              {/* content */}
              {m.shape === 'rect' && (
                <rect
                  x="10"
                  y="10"
                  width="60"
                  height="40"
                  className={m.anim}
                  style={{ fill: 'currentColor', opacity: 0.6 }}
                />
              )}
              {m.shape === 'polyline' && (
                <polyline
                  points={m.points}
                  stroke="currentColor"
                  fill="none"
                  strokeWidth="3"
                  className={m.anim}
                />
              )}
              {m.shape === 'circle' && (
                <circle
                  cx={m.cx}
                  cy={m.cy}
                  r={m.r}
                  fill="currentColor"
                  className={m.anim}
                />
              )}
              {m.shape === 'line' && (
                <line
                  x1={m.x1} y1={m.y1} x2={m.x2} y2={m.y2}
                  stroke="currentColor" strokeWidth="4"
                  className={m.anim}
                />
              )}
            </g>
          ))}
        </svg>
      </ServiceHero>

      {/* AI & Machine Learning */}
      <ServiceHero
        id="ai"
        title="AI & Machine Learning"
        subtitle="From prototyping to production"
        description="Building and deploying neural network models in Python: data preprocessing,
                  training with TensorFlow/PyTorch, and integrating ML APIs. Experienced in
                  MATLAB simulations and GPU-accelerated workflows."
      >
        <svg
          className="absolute inset-0 w-80 h-80 mx-auto my-auto text-pink-400"
          viewBox="0 0 320 320"
          fill="none"
          stroke="currentColor"
          strokeWidth="4"
        >
          {/* Brain half */}
          <g>
            <path
              d="M100,160
                 C60,140 80,100 120,90
                 S180,100 160,140
                 S140,180 100,160"
              fill="none"
              stroke="currentColor"
              strokeWidth="3"
            />
            {/* neuron lines */}
            {[ { x1: 90,y1:140,x2:160,y2:140 },
               { x1:110,y1:120,x2:160,y2:160 },
               { x1:120,y1:180,x2:160,y2:180 } ].map((l,i) => (
              <g key={i} className="animate-pulse" style={{ animationDelay: `${i*0.3}s` }}>
                <line
                  x1={l.x1} y1={l.y1} x2={l.x2} y2={l.y2}
                  stroke="currentColor" strokeWidth="2"
                  strokeDasharray="4 4"
                />
                <circle cx={l.x1} cy={l.y1} r="4" fill="currentColor"/>
              </g>
            ))}
          </g>

          {/* Gear half */}
          <g className="animate-spin" style={{ transformOrigin: '220px 160px' }}>
            <circle cx="220" cy="160" r="60" stroke="currentColor" fill="none"/>
            {[...Array(16)].map((_, j) => {
              const angle = (360 / 16) * j;
              return (
                <rect
                  key={j}
                  x="219" y="100"
                  width="2"
                  height="20"
                  fill="currentColor"
                  transform={`rotate(${angle} 220 160)`}
                />
              );
            })}
          </g>
        </svg>
      </ServiceHero>
    </>
  );
}
