// src/pages/About.jsx
import React from 'react';
import ServiceHero from '../components/ServiceHero';

export default function About() {
  return (
    <>
      {/* Hero Cloud & DevOps */}
      <ServiceHero
        id="cloud"
        title="Cloud & DevOps Services"
        subtitle="Robust, scalable infrastructure and CI/CD pipelines"
        description="I deliver end-to-end cloud solutions: designing and maintaining OpenStack environments, automating deployments with Kubernetes, Docker, Ansible and managing storage with Ceph. Offering fully automated CI/CD, monitoring with Prometheus & Grafana, and GitOps workflows."
      >
        {/* Przykładowa CSS animation: wirujący pierścień */}
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="w-40 h-40 border-4 border-neonAccent rounded-full animate-spin-slow"></div>
        </div>
      </ServiceHero>

      {/* Hero Automation & SRE */}
      <ServiceHero
        id="automation"
        title="Automation & SRE Excellence"
        subtitle="Infrastructure as Code & Reliable Operations"
        description="Specializing in writing Ansible playbooks, Terraform modules, and Helm charts. I implement SRE best practices: incident response, runbooks, chaos testing, and ensure 99.9% uptime through proactive monitoring and alerting."
      >
        {/* CSS animation: pulsująca linia */}
        <div className="absolute bottom-8 left-1/4 w-1/2 h-1 bg-neonPrimary animate-pulse-slow"></div>
      </ServiceHero>

      {/* Hero AI & ML */}
      <ServiceHero
        id="ai"
        title="AI & Machine Learning"
        subtitle="From prototyping to production"
        description="Building and deploying neural network models in Python: data preprocessing, training with TensorFlow/PyTorch, and integrating ML APIs. Also experienced in MATLAB simulations and GPU-accelerated workflows."
      >
        {/* CSS animation: migająca kropka */}
        <div className="absolute top-8 right-1/4 w-4 h-4 bg-neonAccent rounded-full animate-pulse-slow"></div>
      </ServiceHero>

      {/* Dodaj więcej bloków dla Web, Security itd. */}
    </>
  );
}
