// src/components/Navbar.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Menu, X } from 'lucide-react';  // jeśli lucide-react jest dostępne

export default function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);

  const scrollToContact = e => {
    e.preventDefault();
    const el = document.getElementById('contact');
    if (el) el.scrollIntoView({ behavior: 'smooth' });
    setMenuOpen(false);
  };

  const navItems = [
    { label: 'Home', to: '/' },
    { label: 'O mnie', to: '/o-mnie' },
    { label: 'Projekty', to: '/projekty' },
    { label: 'CV', to: '/cv' },
  ];

  return (
    <nav className="fixed w-full z-50 bg-gray-900/80 backdrop-blur-sm">
      <div className="container mx-auto flex items-center justify-between p-4">
        <div className="text-2xl font-extrabold text-neonPrimary">Bartosz Golis</div>

        {/* Desktop menu */}
        <ul className="hidden md:flex space-x-8 items-center">
          {navItems.map(item => (
            <li key={item.label}>
              <Link
                to={item.to}
                className="relative group text-gray-200 hover:text-neonAccent transition"
                onClick={() => setMenuOpen(false)}
              >
                {item.label}
                <span className="absolute left-0 bottom-0 w-0 h-1 bg-neonAccent group-hover:w-full transition-all" />
              </Link>
            </li>
          ))}
          <li>
            <a
              href="#contact"
              onClick={scrollToContact}
              className="relative group text-gray-200 hover:text-neonAccent transition"
            >
              Contact
              <span className="absolute left-0 bottom-0 w-0 h-1 bg-neonAccent group-hover:w-full transition-all" />
            </a>
          </li>
        </ul>

        {/* Mobile menu button */}
        <button
          className="md:hidden text-gray-200"
          onClick={() => setMenuOpen(prev => !prev)}
          aria-label="Toggle menu"
        >
          {menuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile dropdown */}
      {menuOpen && (
        <div className="md:hidden bg-gray-900/90 backdrop-blur-sm">
          <ul className="flex flex-col items-center space-y-4 py-6">
            {navItems.map(item => (
              <li key={item.label}>
                <Link
                  to={item.to}
                  className="text-gray-200 text-lg hover:text-neonAccent transition"
                  onClick={() => setMenuOpen(false)}
                >
                  {item.label}
                </Link>
              </li>
            ))}
            <li>
              <a
                href="#contact"
                onClick={scrollToContact}
                className="text-gray-200 text-lg hover:text-neonAccent transition"
              >
                Contact
              </a>
            </li>
          </ul>
        </div>
      )}
    </nav>
);
}
