// src/pages/About.jsx
import React from 'react';
import ServiceHero from '../components/ServiceHero';

export default function About() {
  return (
    <>
      {/* Intro Section - bez zmian */}
      <section className="p-8 bg-gray-900 text-gray-100" data-aos="fade-up">
        <div className="max-w-3xl mx-auto text-center space-y-4">
          <h1 className="text-5xl font-extrabold text-neonPrimary">About Me</h1>
          <p className="text-lg text-gray-300">
            I hold a B.Eng in Industrial Automation with a specialization in Power Systems Control (ASE) from
            Wrocław University of Science and Technology (Oct 2020 – Feb 2024), and an M.Sc in Automation IT
            Systems specializing in Information Technologies Applications (ISA) (Mar 2024 – Jul 2025). After
            graduation, I relocated to Warsaw and embarked on my journey as a Junior NOC Operator managing
            OpenStack cloud environments. Previously, I gained hands-on experience programming PLC controllers
            and designing SCADA interfaces.
          </p>
        </div>
      </section>

      {/* Cloud & DevOps - poprawiona chmurka i zębatki */}
      <ServiceHero
        id="cloud"
        title="Cloud & DevOps Services"
        subtitle="Robust, scalable infrastructure and CI/CD pipelines"
        description="I deliver end-to-end cloud solutions: designing and maintaining OpenStack environments,
                  automating deployments with Kubernetes, Docker, Ansible, and managing storage with Ceph.
                  Offering fully automated CI/CD, advanced monitoring with Prometheus & Grafana, and GitOps workflows."
      >
        <svg className="absolute inset-0 w-64 h-64 mx-auto my-auto" viewBox="0 0 120 80">
          {/* Poprawiony kształt chmurki */}
          <path 
            d="M25,50 Q15,35 30,30 Q25,15 50,20 Q70,10 80,30 Q100,25 95,45 Q110,55 85,60 Q65,75 40,65 Q20,65 25,50" 
            fill="#2d2d2d" 
            stroke="#7f5af0" 
            strokeWidth="1"
          />

          {/* Dwie zębatki w przeciwnych kierunkach */}
          {[
            {cx: 40, cy: 45, r: 10, teeth: 12, color: "#7f5af0", direction: "normal"},
            {cx: 75, cy: 50, r: 8, teeth: 8, color: "#d946ef", direction: "reverse"}
          ].map((gear, i) => (
            <g 
              key={i} 
              className={`animate-spin-slow ${gear.direction === "reverse" ? "[animation-direction:reverse]" : ""}`}
              style={{ transformOrigin: `${gear.cx}px ${gear.cy}px` }}
            >
              {/* Korpus zębatki */}
              <circle 
                cx={gear.cx} 
                cy={gear.cy} 
                r={gear.r} 
                fill="#2d2d2d" 
                stroke={gear.color} 
                strokeWidth="1" 
              />

              {/* Zęby */}
              {[...Array(gear.teeth)].map((_, j) => {
                const angle = (360 / gear.teeth) * j;
                const outerRadius = gear.r * 1.4;
                const innerRadius = gear.r * 0.7;

                return (
                  <rect
                    key={j}
                    x={gear.cx - 1.5}
                    y={gear.cy - innerRadius}
                    width="3"
                    height={outerRadius - innerRadius}
                    fill={gear.color}
                    transform={`rotate(${angle} ${gear.cx} ${gear.cy})`}
                  />
                );
              })}
            </g>
          ))}
        </svg>
      </ServiceHero>

      {/* Automation & SRE - 4 monitory z animacjami */}
      <ServiceHero
        id="automation"
        title="Automation & SRE Excellence"
        subtitle="Infrastructure as Code & Reliable Operations"
        description="Specializing in writing Ansible playbooks, Terraform modules, and Helm charts.
                  I implement SRE best practices: incident response, runbooks, chaos testing,
                  and ensure 99.9% uptime through proactive monitoring and alerting."
      >
        <svg className="absolute inset-0 w-72 h-72 mx-auto my-auto" viewBox="0 0 140 100">
          {/* Biurko */}
          <rect x="10" y="75" width="120" height="5" fill="#444" rx="2" />

          {/* Krzesło - postać siedząca tyłem */}
          <rect x="60" y="70" width="20" height="15" fill="#555" rx="2" />
          <circle cx="70" cy="65" r="5" fill="#666" />

          {/* 4 monitory (2x2) */}
          <g transform="translate(20, 25)">
            {[
              {x: 0, y: 0, content: "K8s"},
              {x: 35, y: 0, content: "TF"},
              {x: 0, y: 30, content: "Ansible"},
              {x: 35, y: 30, content: "Prom"}
            ].map((monitor, i) => (
              <g key={i} transform={`translate(${monitor.x}, ${monitor.y})`}>
                {/* Monitor */}
                <rect 
                  x="0" 
                  y="0" 
                  width="30" 
                  height="25" 
                  fill="#2d2d2d" 
                  stroke="#7f5af0" 
                  strokeWidth="1" 
                  rx="2"
                />

                {/* Podstawa monitora */}
                <rect x="12" y="25" width="6" height="5" fill="#444" />

                {/* Animowana zawartość monitora */}
                <text 
                  x="15" 
                  y="15" 
                  textAnchor="middle" 
                  fill="#d946ef" 
                  fontSize="5"
                  className="animate-pulse"
                >
                  {monitor.content}
                </text>

                {/* Migające elementy */}
                {[...Array(4)].map((_, j) => (
                  <rect
                    key={j}
                    x={5 + j * 6}
                    y={18}
                    width="4"
                    height="2"
                    fill="#7f5af0"
                    className="animate-blink"
                    style={{ animationDelay: `${j * 0.2}s` }}
                  />
                ))}
              </g>
            ))}
          </g>
        </svg>
      </ServiceHero>

      {/* AI & Machine Learning - całkowicie przeprojektowane */}
      <ServiceHero
        id="ai"
        title="AI & Machine Learning"
        subtitle="From prototyping to production"
        description="Building and deploying neural network models in Python: data preprocessing,
                  training with TensorFlow/PyTorch, and integrating ML APIs. Experienced in
                  MATLAB simulations and GPU-accelerated workflows."
      >
        <svg className="absolute inset-0 w-72 h-72 mx-auto my-auto" viewBox="0 0 120 100">
          {/* Robot */}
          <g transform="translate(70, 50)">
            {/* Głowa */}
            <circle cx="0" cy="-20" r="10" fill="#2d2d2d" stroke="#d946ef" strokeWidth="1" />
            {/* Oczy */}
            <circle cx="-4" cy="-22" r="1.5" fill="#7f5af0" className="animate-pulse" />
            <circle cx="4" cy="-22" r="1.5" fill="#7f5af0" className="animate-pulse" />
            {/* Ciało */}
            <rect x="-12" y="-10" width="24" height="20" fill="#2d2d2d" stroke="#d946ef" strokeWidth="1" rx="3" />
            {/* Antena */}
            <line x1="0" y1="-30" x2="0" y2="-35" stroke="#7f5af0" strokeWidth="1">
              <animate attributeName="y2" values="-35;-40;-35" dur="1s" repeatCount="indefinite" />
            </line>
          </g>

          {/* Drabina */}
          <g transform="translate(40, 30)">
            <line x1="0" y1="0" x2="0" y2="60" stroke="#888" strokeWidth="3" />
            <line x1="15" y1="0" x2="15" y2="60" stroke="#888" strokeWidth="3" />
            {[0, 10, 20, 30, 40, 50].map((y, i) => (
              <line key={i} x1="0" y1={y} x2="15" y2={y} stroke="#888" strokeWidth="2" />
            ))}
          </g>

          {/* Inżynier na drabinie */}
          <g transform="translate(45, 35)">
            {/* Głowa */}
            <circle cx="0" cy="0" r="4" fill="#666" />
            {/* Ciało */}
            <rect x="-2" y="4" width="4" height="10" fill="#555" />
            {/* Ręka grzebiąca w robocie */}
            <line 
              x1="0" 
              y1="8" 
              x2="25" 
              y2="0" 
              stroke="#777" 
              strokeWidth="2" 
              className="animate-arm-move"
            />
            {/* Narzędzie */}
            <rect 
              x="23" 
              y="-3" 
              width="4" 
              height="4" 
              fill="#d946ef" 
              className="animate-arm-move"
              style={{ transformOrigin: '25px 0px' }}
            />
          </g>
        </svg>
      </ServiceHero>
    </>
  );
}
