// src/pages/About.jsx
import React from 'react';
import ServiceHero from '../components/ServiceHero';

export default function About() {
  return (
    <>
      {/* Intro Section - bez zmian */}
      <section className="p-8 bg-gray-900 text-gray-100" data-aos="fade-up">
        <div className="max-w-3xl mx-auto text-center space-y-4">
          <h1 className="text-5xl font-extrabold text-neonPrimary">About Me</h1>
          <p className="text-lg text-gray-300">
            I hold a B.Eng in Industrial Automation with a specialization in Power Systems Control (ASE) from
            Wrocław University of Science and Technology (Oct 2020 – Feb 2024), and an M.Sc in Automation IT
            Systems specializing in Information Technologies Applications (ISA) (Mar 2024 – Jul 2025). After
            graduation, I relocated to Warsaw and embarked on my journey as a Junior NOC Operator managing
            OpenStack cloud environments. Previously, I gained hands-on experience programming PLC controllers
            and designing SCADA interfaces.
          </p>
        </div>
      </section>

      {/* Cloud & DevOps - złożone zębatki wewnątrz chmurki */}
      <ServiceHero
        id="cloud"
        title="Cloud & DevOps Services"
        subtitle="Robust, scalable infrastructure and CI/CD pipelines"
        description="I deliver end-to-end cloud solutions: designing and maintaining OpenStack environments,
                  automating deployments with Kubernetes, Docker, Ansible, and managing storage with Ceph.
                  Offering fully automated CI/CD, advanced monitoring with Prometheus & Grafana, and GitOps workflows."
      >
        <svg className="absolute inset-0 w-72 h-72 mx-auto my-auto" viewBox="0 0 140 100">
          {/* Chmurka - bardziej organiczny kształt */}
          <path 
            d="M35,50 Q25,35 40,25 Q35,10 60,15 Q85,5 95,30 Q115,25 110,50 Q125,60 100,65 Q75,80 50,70 Q30,70 35,50" 
            fill="#2d2d2d" 
            stroke="#7f5af0" 
            strokeWidth="1"
            strokeDasharray="4,2"
          />

          {/* Trzy zębatki w różnych rozmiarach i kierunkach */}
          {[
            {cx: 50, cy: 45, r: 12, teeth: 16, color: "#7f5af0", speed: "slow", direction: "normal"},
            {cx: 75, cy: 50, r: 8, teeth: 10, color: "#d946ef", speed: "medium", direction: "reverse"},
            {cx: 35, cy: 60, r: 6, teeth: 8, color: "#2cb67d", speed: "fast", direction: "normal"}
          ].map((gear, i) => (
            <g 
              key={i} 
              className={`animate-spin-${gear.speed} ${gear.direction === "reverse" ? "[animation-direction:reverse]" : ""}`}
              style={{ transformOrigin: `${gear.cx}px ${gear.cy}px` }}
            >
              {/* Zęby */}
              {[...Array(gear.teeth)].map((_, j) => {
                const angle = (360 / gear.teeth) * j;
                return (
                  <rect
                    key={j}
                    x={gear.cx - 1.5}
                    y={gear.cy - gear.r * 1.2}
                    width="3"
                    height={gear.r * 0.8}
                    fill={gear.color}
                    transform={`rotate(${angle} ${gear.cx} ${gear.cy})`}
                  />
                );
              })}

              {/* Centralny otwór */}
              <circle 
                cx={gear.cx} 
                cy={gear.cy} 
                r={gear.r * 0.4} 
                fill="#2d2d2d" 
                stroke="#2d2d2d" 
                strokeWidth="1" 
              />
            </g>
          ))}

          {/* Linie łączące zębatki */}
          <line x1="50" y1="45" x2="75" y2="50" stroke="#d946ef" strokeWidth="1" strokeDasharray="2,2" />
          <line x1="50" y1="45" x2="35" y2="60" stroke="#2cb67d" strokeWidth="1" strokeDasharray="2,2" />
        </svg>
      </ServiceHero>

      {/* Automation & SRE - 4 monitory z animacjami i postacią */}
      <ServiceHero
        id="automation"
        title="Automation & SRE Excellence"
        subtitle="Infrastructure as Code & Reliable Operations"
        description="Specializing in writing Ansible playbooks, Terraform modules, and Helm charts.
                  I implement SRE best practices: incident response, runbooks, chaos testing,
                  and ensure 99.9% uptime through proactive monitoring and alerting."
      >
        <svg className="absolute inset-0 w-80 h-80 mx-auto my-auto" viewBox="0 0 160 120">
          {/* Biurko */}
          <rect x="10" y="85" width="140" height="5" fill="#444" rx="2" />

          {/* Krzesło z postacią */}
          <g transform="translate(70, 65)">
            {/* Siedzisko */}
            <rect x="-10" y="0" width="20" height="15" fill="#555" rx="2" />
            {/* Oparcie */}
            <rect x="-12" y="-25" width="24" height="25" fill="#666" rx="2" />
            {/* Głowa */}
            <circle cx="0" cy="-35" r="6" fill="#777" />
          </g>

          {/* 4 monitory w układzie 2x2 */}
          <g transform="translate(20, 20)">
            {[
              {x: 0, y: 0, content: "K8s", color: "#7f5af0"},
              {x: 45, y: 0, content: "TF", color: "#d946ef"},
              {x: 0, y: 40, content: "Ansible", color: "#2cb67d"},
              {x: 45, y: 40, content: "Prometheus", color: "#ff7e6b"}
            ].map((monitor, i) => (
              <g key={i} transform={`translate(${monitor.x}, ${monitor.y})`}>
                {/* Monitor */}
                <rect 
                  x="0" 
                  y="0" 
                  width="40" 
                  height="35" 
                  fill="#2d2d2d" 
                  stroke={monitor.color} 
                  strokeWidth="1" 
                  rx="3"
                />

                {/* Podstawa monitora */}
                <rect x="15" y="35" width="10" height="8" fill="#444" />

                {/* Zawartość monitora - animowana */}
                <text 
                  x="20" 
                  y="18" 
                  textAnchor="middle" 
                  fill={monitor.color} 
                  fontSize="6"
                  fontWeight="bold"
                >
                  {monitor.content}
                </text>

                {/* Animowane elementy technologiczne */}
                {monitor.content === "K8s" && (
                  <g transform="translate(10, 25)">
                    <circle cx="5" cy="0" r="3" fill="#7f5af0" className="animate-pulse" />
                    <circle cx="15" cy="0" r="2" fill="#7f5af0" className="animate-pulse" style={{ animationDelay: "0.3s" }} />
                    <circle cx="25" cy="0" r="3" fill="#7f5af0" className="animate-pulse" style={{ animationDelay: "0.6s" }} />
                  </g>
                )}

                {monitor.content === "TF" && (
                  <g transform="translate(10, 25)">
                    <rect x="0" y="0" width="5" height="5" fill="#d946ef" className="animate-bounce" />
                    <rect x="10" y="0" width="5" height="5" fill="#d946ef" className="animate-bounce" style={{ animationDelay: "0.2s" }} />
                    <rect x="20" y="0" width="5" height="5" fill="#d946ef" className="animate-bounce" style={{ animationDelay: "0.4s" }} />
                  </g>
                )}

                {monitor.content === "Ansible" && (
                  <g transform="translate(10, 25)">
                    <path d="M0,0 L10,5 L0,10 Z" fill="#2cb67d" className="animate-spin-medium" style={{ transformOrigin: "5px 5px" }} />
                  </g>
                )}

                {monitor.content === "Prometheus" && (
                  <g transform="translate(20, 25)">
                    <line x1="0" y1="0" x2="0" y2="8" stroke="#ff7e6b" strokeWidth="1">
                      <animate attributeName="y2" values="8;4;8" dur="1.5s" repeatCount="indefinite" />
                    </line>
                    <line x1="5" y1="2" x2="5" y2="8" stroke="#ff7e6b" strokeWidth="1">
                      <animate attributeName="y2" values="8;6;8" dur="1.5s" repeatCount="indefinite" begin="0.3s" />
                    </line>
                  </g>
                )}
              </g>
            ))}
          </g>
        </svg>
      </ServiceHero>

      {/* AI & Machine Learning - całkowicie przeprojektowana */}
      <ServiceHero
        id="ai"
        title="AI & Machine Learning"
        subtitle="From prototyping to production"
        description="Building and deploying neural network models in Python: data preprocessing,
                  training with TensorFlow/PyTorch, and integrating ML APIs. Experienced in
                  MATLAB simulations and GPU-accelerated workflows."
      >
        <svg className="absolute inset-0 w-80 h-80 mx-auto my-auto" viewBox="0 0 140 120">
          {/* Robot */}
          <g transform="translate(90, 70)">
            {/* Głowa */}
            <circle cx="0" cy="-25" r="15" fill="#2d2d2d" stroke="#d946ef" strokeWidth="1" />

            {/* Oczy - animowane */}
            <circle cx="-8" cy="-28" r="3" fill="#7f5af0">
              <animate attributeName="r" values="3;1;3" dur="2s" repeatCount="indefinite" />
            </circle>
            <circle cx="8" cy="-28" r="3" fill="#7f5af0">
              <animate attributeName="r" values="3;1;3" dur="2s" repeatCount="indefinite" begin="0.5s" />
            </circle>

            {/* Antena */}
            <line x1="0" y1="-40" x2="0" y2="-50" stroke="#7f5af0" strokeWidth="1">
              <animate attributeName="y2" values="-50;-45;-50" dur="1s" repeatCount="indefinite" />
            </line>
            <circle cx="0" cy="-50" r="2" fill="#2cb67d" className="animate-pulse" />

            {/* Ciało */}
            <rect x="-20" y="-10" width="40" height="30" fill="#2d2d2d" stroke="#d946ef" strokeWidth="1" rx="5" />

            {/* Wyświetlacz na ciele */}
            <rect x="-15" y="0" width="30" height="10" fill="#1a1a1a" rx="2" />
            <path d="M-10,5 L0,2 L10,5 L5,8 L-5,8 Z" fill="#ff7e6b" className="animate-pulse" />
          </g>

          {/* Drabina */}
          <g transform="translate(40, 20)">
            <line x1="0" y1="0" x2="0" y2="80" stroke="#888" strokeWidth="3" />
            <line x1="20" y1="0" x2="20" y2="80" stroke="#888" strokeWidth="3" />
            {[0, 15, 30, 45, 60, 75].map((y, i) => (
              <line key={i} x1="0" y1={y} x2="20" y2={y} stroke="#888" strokeWidth="2" />
            ))}
          </g>

          {/* Inżynier na drabinie */}
          <g transform="translate(45, 40)">
            {/* Głowa */}
            <circle cx="0" cy="0" r="6" fill="#666" />
            {/* Hełm */}
            <path d="M-6,0 Q0,-10 6,0" fill="#777" stroke="#888" strokeWidth="1" />
            <circle cx="0" cy="-3" r="2" fill="#ff7e6b" className="animate-pulse" />

            {/* Ciało */}
            <rect x="-4" y="6" width="8" height="20" fill="#555" rx="2" />

            {/* Ręce */}
            <line 
              x1="-5" 
              y1="15" 
              x2="-30" 
              y2="10" 
              stroke="#777" 
              strokeWidth="3" 
              className="animate-arm-move"
            />
            <line 
              x1="5" 
              y1="15" 
              x2="15" 
              y2="25" 
              stroke="#777" 
              strokeWidth="3" 
            />

            {/* Narzędzie w ręce */}
            <g transform="translate(-30, 10)" className="animate-arm-move">
              <rect x="-5" y="-3" width="10" height="6" fill="#d946ef" rx="2" />
              <circle cx="0" cy="0" r="3" fill="#7f5af0" />
              <line x1="0" y1="0" x2="0" y2="10" stroke="#2cb67d" strokeWidth="2">
                <animate attributeName="y2" values="10;15;10" dur="1.5s" repeatCount="indefinite" />
              </line>
            </g>

            {/* Nogi */}
            <line x1="-3" y1="26" x2="-5" y2="40" stroke="#666" strokeWidth="3" />
            <line x1="3" y1="26" x2="5" y2="40" stroke="#666" strokeWidth="3" />
          </g>

          {/* Elementy AI - sieć neuronowa */}
          <g transform="translate(100, 30)" stroke="#7f5af0" strokeWidth="0.5" fill="none">
            {/* Warstwy */}
            {[0, 20, 40].map((x, i) => (
              <g key={i}>
                {[...Array(5)].map((_, j) => (
                  <circle key={j} cx={x} cy={j*10} r="2" fill="#d946ef" />
                ))}
              </g>
            ))}

            {/* Połączenia */}
            {[...Array(5)].map((_, j) => (
              [...Array(5)].map((_, k) => (
                <line 
                  key={`${j}-${k}`} 
                  x1="0" 
                  y1={j*10} 
                  x2="20" 
                  y2={k*10} 
                  strokeDasharray="2,2"
                />
              ))
            ))}
            {[...Array(5)].map((_, j) => (
              [...Array(5)].map((_, k) => (
                <line 
                  key={`${j}-${k}-2`} 
                  x1="20" 
                  y1={j*10} 
                  x2="40" 
                  y2={k*10} 
                  strokeDasharray="2,2"
                />
              ))
            ))}
          </g>
        </svg>
      </ServiceHero>
    </>
  );
}
